const StyledMediaTab = styled.section`
  background: #f9f9f9;
  z-index: 999;
  position: fixed;
  left: 0;
  right: 0;
  scroll-behavior: smooth;
  
  .inner-menu {
    //height: 80px;
    background: #f9f9f9;
    z-index: 9999;

    ul {
      display: flex;
      width: 100%;
      justify-content: center;

      li {
        &:not(:nth-last-of-type(1)) {
          margin-right: 50px;
        }

        a {
          font-size: 16px;
          line-height: 24px;
          color: #000;
          height: 80px;
          justify-content: center;
          align-items: center;
          display: flex;
          text-transform: uppercase;
          position: relative;
          svg{
            margin-right: 10px;
          }

          &:after {
            content: '';
            position: absolute;
            height: 1px;
            background-color: #F2EEE8;
            width: 0;
            left: 0;
            bottom: 30px;

          }

          &:hover {
            color: #c9c9c9 !important;
            text-decoration: none!important;

            &:after {
              width: 100%;
            }
          }

          &.active:after {
            width: 100%;
          }
        }
      }
    }
  }

  @media (max-width: 991px) {
    padding-top: 100px;
    .InnerBanner__parallax {
      padding-top: calc(200 / 345 * 100%);
    }

    .inner-menu ul li:not(:nth-last-of-type(1)) {
      margin-right: 30px;
    }

    .inner-menu ul li a {
      font-size: 12px;
    }
  }

  @media (max-width: 767px) {

    .inner-menu {
      height: 60px;

      ul {
        justify-content: flex-start;
        padding-left: 15px;
        padding-right: 15px;

        li {
          width: 100%;
          margin: 0 !important;

          &:not(:nth-of-type(1)) {
            display: none;
          }

          a {
            width: 100%;
            justify-content: flex-start;
            height: 60px;

            &:after {
              display: none;
            }

            &:nth-of-type(1):after {
              content: '';
              background-repeat: no-repeat;
              border-radius: 50%;
              background-position: center;
              height: 24px;
              width: 24px;
              display: flex;
              align-items: center;
              justify-content: center;
              background-color: #F2EEE8;
              //transform: rotate(90deg);
              position: absolute;
              right: 0;
              left: auto;
              top: 0;
              bottom: 0;
              margin: auto;
            }
          }
        }
      }
    }
  }


  @media(max-width: 767px){
    display: none;
  }

`;

export default React.memo(Navbar);

